import {Component} from 'react'

import {Route, Switch} from 'react-router-dom'
import {AiFillHome} from 'react-icons/ai'
import {HiFire} from 'react-icons/hi'
import {CgPlayListAdd} from 'react-icons/cg'
import {SiYoutubegaming} from 'react-icons/si'
import NxtThemeContext from './Context'

import Login from './components/Login'

import Home from './components/Home'

import './App.css'

// Replace your code here
class App extends Component {
  state = {
    isDarkTheme: false,
    sideNavList: [
      {id: 1, logo: AiFillHome, path: '/', text: 'Home', active: true},
      {id: 2, logo: HiFire, path: '/', text: 'Trending', active: false},
      {id: 3, logo: SiYoutubegaming, path: '/', text: 'Gaming', active: false},
      {
        id: 4,
        logo: CgPlayListAdd,
        path: '/',
        text: 'Saved Videos',
        active: false,
      },
    ],
    savedVideos: [],
  }

  onClickActive = id => {
      const {sideNavList} = this.sae
    const newList = sideNavList.map(eachItem =>
      eachItem.id === id
        ? {
            id: eachItem.id,
            logo: eachItem.logo,
            path: eachItem.path,
            text: eachItem.text,
            active: true,
          }
        : {
            id: eachItem.id,
            logo: eachItem.logo,
            path: eachItem.path,
            text: eachItem.text,
            active: false,
          },
    )
    this.setState({initialNavList: newList})
  }

  toggleTheme = () => {
    this.setState(prevState => ({isDarkTheme: !prevState.isDarkTheme}))
  }

  render() {
    const {isDarkTheme, sideNavList, savedVideos} = this.state
    return (
      <NxtThemeContext.Provider
        value={{
          isDarkTheme,
          toggleTheme: this.toggleTheme,
          sideNavList,
          savedVideos,
        }}
      >
        <Switch>
          <Route exact path="/login" component={Login} />
          <Route exact path="/" component={Home} />
        </Switch>
      </NxtThemeContext.Provider>
    )
  }
}

export default App
