import {Component} from 'react'

import Cookies from 'js-cookie'
import ReactPlayer from 'react-player'

import {formatDistanceToNow} from 'date-fns'

import SideBar from '../SideBar'
import Header from '../Header'

import NxtThemeContext from '../../Context'
import {
  VideoItemDetailsContainer,
  VideoItemAndSideBar,
  VideoContainer,
  VideoTitle,
  Video,
  SmallContainer,
  SmallDetails,
} from './StyledComponents'

const apiStatus = {
  loading: 'LOADING',
  success: 'SUCCESS',
  failure: 'FAILURE',
}

class VideoItemDetails extends Component {
  state = {VideoDetails: {}, status: apiStatus.loading}

  componentDidMount() {
    this.getVideoDetails()
  }

  getVideoDetails = async () => {
    this.setState({status: apiStatus.loading})
    const jwtToken = Cookies.get('jwt_token')
    const {match} = this.props
    const {params} = match
    const {id} = params
    const url = `https://apis.ccbp.in/videos/${id}`
    console.log(url)
    const options = {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
    }
    const response = await fetch(url, options)
    const data = await response.json()
    const fetchedData = data.video_details
    console.log(fetchedData)
    if (response.ok) {
      const updatedData = {
        channel: {
          name: fetchedData.channel.name,
          profileImageUrl: fetchedData.channel.profile_image_url,
          subscriberCount: fetchedData.channel.subscriber_count,
        },
        description: fetchedData.description,
        id: fetchedData.id,
        publishedAt: fetchedData.published_at,
        thumbnailUrl: fetchedData.thumbnail_url,
        title: fetchedData.title,
        videoUrl: fetchedData.video_url,
        viewCount: fetchedData.view_count,
      }
      this.setState({VideoDetails: updatedData})
      console.log(updatedData)
    }
  }

  render() {
    const {VideoDetails, status} = this.state
    const time = formatDistanceToNow(new Date(VideoDetails.publishedAt))
    console.log(VideoDetails, status, time)
    return (
      <NxtThemeContext.Consumer>
        {value => {
          const {isDarkTheme} = value
          const theme = isDarkTheme ? 'dark' : ''
          console.log(theme)
          return (
            <VideoItemDetailsContainer theme={theme}>
              <Header />
              <VideoItemAndSideBar>
                <SideBar />
                <VideoContainer theme={theme}>
                  <Video>
                    <ReactPlayer
                      style={{flexGrow: '1', flexShrink: '1'}}
                      height="70vh"
                      url={VideoDetails.videoUrl}
                      controls
                    />
                  </Video>
                  <VideoTitle theme={theme}>{VideoDetails.title}</VideoTitle>
                  <SmallContainer spaceBetween >
                      <SmallContainer>
                          <SmallDetails>{VideoDetails.view}</SmallDetails>
                      </SmallContainer>
                  </SmallContainer>
                </VideoContainer>
              </VideoItemAndSideBar>
            </VideoItemDetailsContainer>
          )
        }}
      </NxtThemeContext.Consumer>
    )
  }
}

export default VideoItemDetails
