import {formatDistanceToNow} from 'date-fns'
import {VscCircleFilled} from 'react-icons/vsc'
import NxtThemeContext from '../../Context'
import {
  ThumbnailContainer,
  ThumbnailImage,
  ThumbnailTextContainer,
  ChannelLogo,
  Title,
  Name,
  ViewsAndTimeContainer,
} from './StyledComponents'

const VideoThumbnail = props => {
  const {details} = props
  const a = formatDistanceToNow(new Date(details.publishedAt))
  const c = a.split(' ')
  const b = c.splice(1, 3).join()
console.log
  return (
    <NxtThemeContext.Consumer>
      {value => {
        const {isDarkTheme} = value
        const theme = isDarkTheme ? 'dark' : ''
        return (
          <ThumbnailContainer theme={theme}>
            <ThumbnailImage src={details.thumbnailUrl} alt="video thumbnail" />
            <ThumbnailTextContainer>
              <ChannelLogo
                src={details.channel.profileImageUrl}
                alt="channel logo"
              />
              <div>
                <Title>{details.title}</Title>
                <Name>{details.channel.name}</Name>
                <ViewsAndTimeContainer>
                  <Name>{details.viewCount} views</Name>
                  <Name as="div">
                    <VscCircleFilled />
                  </Name>
                  <Name>{a}</Name>
                </ViewsAndTimeContainer>
              </div>
            </ThumbnailTextContainer>
          </ThumbnailContainer>
        )
      }}
    </NxtThemeContext.Consumer>
  )
}
export default VideoThumbnail
