import {withRouter} from 'react-router-dom'
import Cookies from 'js-cookie'
import {Popup} from 'reactjs-popup'
import NxtThemeContext from '../../Context'
import {
  LogoutButton,
  ModalContainer,
  ModalButton,
  modalText,
} from './StyledComponents'

const Logout = props => (
  <NxtThemeContext.Consumer>
    {value => {
      const {isDarkTheme} = value
      const theme = isDarkTheme ? 'dark' : ''
      const redirectToLoginPage = () => {
        Cookies.remove('jwt_token')
        const {history} = props
        history.replace('/login')
      }

      return (
        <Popup
          modal
          trigger={
            <LogoutButton type="button" theme={theme}>
              Logout
            </LogoutButton>
          }
        >
          {close => (
            <ModalButton>
              <modalText>Are you sure you want to logout ?</modalText>
              <ModalButton onClick={() => close()} type="button">
                Cancel
              </ModalButton>
              <ModalButton onClick={redirectToLoginPage} type="button">
                Confirm
              </ModalButton>
            </ModalButton>
          )}
        </Popup>
      )
    }}
  </NxtThemeContext.Consumer>
)

export default withRouter(Logout)
