import {Component} from 'react'
import {HiFire} from 'react-icons/hi'
import Cookies from 'js-cookie'
import NxtThemeContext from '../../Context'
import {
  GamesContainer,
  SideBarAndContentContainer,
  GamesContentContainer,
  GamesBanner,
  GamesLogoContainer,
  GamesTitle,
  GamesList,
} from './StyledComponents'

import Header from '../Header'
import SideBar from '../SideBar'
import LoaderBox from '../LoaderBox'

const apiStatus = {
  loading: 'LOADING',
  success: 'SUCCESS',
  failure: 'FAILURE',
}

class Gaming extends Component {
  state = {gamesList: [], status: apiStatus.loading}

  componentDidMount() {
    this.getTrendingVideos()
  }

  updateData = data => ({
    channel: {
      name: data.channel.name,
      profileImageUrl: data.channel.profile_image_url,
    },
    id: data.id,
    publishedAt: data.published_at,
    thumbnailUrl: data.thumbnail_url,
    title: data.title,
    viewCount: data.view_count,
  })

  getTrendingVideos = async () => {
    this.setState({status: apiStatus.loading})
    const jwtToken = Cookies.get('jwt_token')
    const options = {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
    }
    const url = 'https://apis.ccbp.in/videos/gaming'
    const response = await fetch(url, options)
    const data = await response.json()
    console.log(data.videos)
    if (response.ok) {
      const fetchedData = data.videos.map(eachItem => this.updateData(eachItem))
      this.setState({status: apiStatus.success, gamesList: fetchedData})
    } else {
      this.setState({status: apiStatus.failure})
    }
  }

  switchRender = theme => {
    const {gamesList, status} = this.state
    switch (status) {
      case 'LOADING':
        return <LoaderBox />
      case 'SUCCESS':
        return (
          <GamesContentContainer>
            <GamesBanner theme={theme}>
              <GamesLogoContainer theme={theme}>
                <HiFire />
              </GamesLogoContainer>
              <GamesTitle theme={theme}>Trending Videos</GamesTitle>
            </GamesBanner>
            <GamesList>
              {gamesList.map(eachItem => (
                <li key={eachItem.id}>
                {each}
                </li>
              ))}
            </GamesList>
          </GamesContentContainer>
        )
      case 'FAILURE':
        return <LoaderBox />
      default:
        return null
    }
  }

  render() {
    return (
      <NxtThemeContext.Consumer>
        {value => {
          const {isDarkTheme} = value
          const theme = isDarkTheme ? 'dark' : ''
          return (
            <GamesContainer theme={theme} data-testid="savedVideos">
              <Header />
              <SideBarAndContentContainer>
                <SideBar />
                {this.switchRender(theme)}
              </SideBarAndContentContainer>
            </GamesContainer>
          )
        }}
      </NxtThemeContext.Consumer>
    )
  }
}
export default Gaming
