import styled from 'styled-components'

export const SideBarContainer = styled.ul`
  list-style: none;
  display: flex;
  justify-content: space-between;
  height: 88vh;
  margin: 0px;
  padding: 0px;
  background-color: ${props =>
    props.theme === 'dark' ? '#313131' : '#ffffff'};
`
export const SideNavBar = styled.ul`
  width: 20vw;
  list-style: none;
  display: flex;
  flex-direction: column;
  padding: 0px;
`
export const SideNavItem = styled.li`
  flex-grow: 1;
  padding: 5px;
  display: flex;
  align-items: center;
  background-color: ${props =>
    props.theme === 'dark' ? '#313131' : '#f8fafc'};
  color: ${props => (props.active ? '#909090' : '#606060')};
  font-size: 20px;
`
export const NavItemText = styled.p`
  font-family: 'Roboto';
  font-size: 16px;
  padding: 0px;
  margin: 10px;
  font-weight: 600;
  color: ${props => (props.theme === 'drak' ? '#cbd5e1' : '#3')};
  opacity: 0.6;
`
