import styled from 'styled-components'

export const SideNavItem = styled.li`
  flex-grow: 1;
  padding: 5px;
  display: flex;
  align-items: center;
  background-color: ${props =>
    props.theme === 'dark' ? '#313131' : '#f8fafc'};
  color: ${props => {
      if(props.active==="active"){
          return (props.theme === 'dark' ? '#909090' : '#606060');
      }
      else{
          return(props.theme === 'dark' ? '#909090' : '#606060');
      }
      }
  }
  font-size: 20px;
  opacity: ${props => (props.active === 'active' ? '2' : '0.8')};
`
export const NavItemText = styled.p`
  font-family: 'Roboto';
  font-size: 16px;
  padding: 0px;
  margin: 10px;
  font-weight: 600;
  color: ${props => (props.theme === 'dark' ? '#cbd5e1' : '#181818')};
`
