import {Component} from 'react'

import Cookies from 'js-cookie'
import ReactPlayer from 'react-player'

import {VscCircleFilled} from 'react-icons/vsc'

import {formatDistanceToNow} from 'date-fns'

import SideBar from '../SideBar'
import Header from '../Header'
import LoaderBox from '../LoaderBox'

import NxtThemeContext from '../../Context'
import {
  VideoItemDetailsContainer,
  VideoItemAndSideBar,
  VideoContainer,
  VideoTitle,
  Video,
  SmallContainer,
  SmallDetails,
  HorizontalLine,
} from './StyledComponents'

const apiStatus = {
  loading: 'LOADING',
  success: 'SUCCESS',
  failure: 'FAILURE',
}

class VideoItemDetails extends Component {
  state = {VideoDetails: {}, status: apiStatus.loading, time: ''}

  componentDidMount() {
    this.getVideoDetails()
  }

  getVideoDetails = async () => {
    this.setState({status: apiStatus.loading})
    const jwtToken = Cookies.get('jwt_token')
    const {match} = this.props
    const {params} = match
    const {id} = params
    const url = `https://apis.ccbp.in/videos/${id}`
    console.log(url)
    const options = {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
    }
    const response = await fetch(url, options)
    const data = await response.json()
    const fetchedData = data.video_details
    console.log(fetchedData)
    if (response.ok) {
      const updatedData = {
        channel: {
          name: fetchedData.channel.name,
          profileImageUrl: fetchedData.channel.profile_image_url,
          subscriberCount: fetchedData.channel.subscriber_count,
        },
        description: fetchedData.description,
        id: fetchedData.id,
        publishedAt: fetchedData.published_at,
        thumbnailUrl: fetchedData.thumbnail_url,
        title: fetchedData.title,
        videoUrl: fetchedData.video_url,
        viewCount: fetchedData.view_count,
      }
      const a = formatDistanceToNow(new Date(updatedData.publishedAt)).split(
        ' ',
      )
      console.log(a)
      this.setState({
        VideoDetails: updatedData,
        status: apiStatus.success,
        time: `${a[1]} ${a[2]} ago`,
      })
      console.log(updatedData)
    } else {
      this.setState({status: apiStatus.failure})
    }
  }

  renderVideo = (theme, savedVideos) => {
    const {VideoDetails, time} = this.state
    const save = Vi
    return (
      <VideoContainer theme={theme}>
        <Video>
          <ReactPlayer
            width="100%"
            height="100%"
            url={VideoDetails.videoUrl}
            controls
          />
        </Video>
        <VideoTitle theme={theme}>{VideoDetails.title}</VideoTitle>
        <SmallContainer spaceBetween>
          <SmallContainer>
            <SmallDetails>{VideoDetails.viewCount} views</SmallDetails>
            <SmallDetails dot>
              <VscCircleFilled />
            </SmallDetails>
            <SmallDetails>{time}</SmallDetails>
          </SmallContainer>
        </SmallContainer>
        <HorizontalLine>
          <hr />
        </HorizontalLine>
      </VideoContainer>
    )
  }

  switchRender = (theme, savedVideos) => {
    const {status} = this.state
    switch (status) {
      case 'LOADING':
        return <LoaderBox />
      case 'SUCCESS':
        return this.renderVideo(theme, savedVideos)
      default:
        return null
    }
  }

  render() {
    return (
      <NxtThemeContext.Consumer>
        {value => {
          const {isDarkTheme, savedVideos} = value
          const theme = isDarkTheme ? 'dark' : ''
          return (
            <VideoItemDetailsContainer theme={theme}>
              <Header />
              <VideoItemAndSideBar>
                <SideBar />
                {this.switchRender(theme, savedVideos)}
              </VideoItemAndSideBar>
            </VideoItemDetailsContainer>
          )
        }}
      </NxtThemeContext.Consumer>
    )
  }
}

export default VideoItemDetails
