import {Component} from 'react'

import Cookies from 'js-cookie'

import NxtThemeContext from '../../Context'

import {
  LoginContainer,
  LoginForm,
  NxtWedLogo,
  InputLabel,
  UserInput,
  CheckboxContainer,
  CheckboxLabel,
  InputCheckBox,
  LoginButton,
  ErrorMsg,
} from './StyledContainer'

class Login extends Component {
  state = {
    username: '',
    password: '',
    displayPassword: false,
    displayErrorMsg: false,
    errorMsg: '',
  }

  submitSuccess = jwtToken => {
    Cookies.set('jwt_token', jwtToken, {
      expires: 30,
      path: '/',
    })
    const {history} = this.props
    history.replace('/')
  }

  submitFailure = errorMsg => {
    this.setState(prevState => ({
      displayErrorMsg: !prevState.displayErrorMsg,
      errorMsg: {errorMsg},
    }))
  }

  postLoginDetails = async event => {
    event.preventDefault()
    const {username, password} = this.state
    const userDetails = {
      username,
      password,
    }
    const url = 'https://apis.ccbp.in/login'
    const options = {
      method: 'POST',
      body: JSON.stringify(userDetails),
    }
    const response = await fetch(url, options)
    const data = await response.json()
    if (response.ok) {
      this.submitSuccess(data.jwt_token)
      this.setState({
        username: '',
        password: '',
        displayErrorMsg: false,
        displayPassword: false,
        errorMsg: '',
      })
    } else {
      this.submitFailure(data.error_msg)
    }
  }

  updateUsername = event => {
    this.setState({username: event.target.value})
  }

  updatePassword = event=>{
      this.setState({password: event})
  }

  render() {
    const {
      username,
      password,
      displayPassword,
      displayErrorMsg,
      errorMsg,
    } = this.state
    return (
      <NxtThemeContext.Consumer>
        {value => {
          const {isDarkTheme} = value
          return (
            <LoginContainer onSubmit={this.postLoginDetails}>
              <LoginForm>
                <NxtWedLogo
                  src={
                    isDarkTheme
                      ? 'https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-dark-theme-img.png'
                      : 'https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-light-theme-img.png'
                  }
                  alt="nxt watch logo"
                />
                <InputLabel htmlFor="user_name">USERNAME</InputLabel>
                <UserInput
                  type="text"
                  id="user_name"
                  onChange={this.updateUsername}
                  value={username}
                  placeholder="Username"
                />
                <InputLabel htmlFor="password">PASSWORD</InputLabel>
                <UserInput
                  type={displayPassword ? 'text' : 'password'}
                  id="password"
                  onChange={this.updatePassword}
                  value={password}
                  placeholder="Password"
                />
                <CheckboxContainer>
                  <InputCheckBox
                    id="show-password"
                    type="checkbox"
                    onClick={this.togglePasswordDisplay}
                  />
                  <CheckboxLabel htmlFor="show-password">
                    Show Password
                  </CheckboxLabel>
                </CheckboxContainer>
                <LoginButton type="submit">Login</LoginButton>
                {displayErrorMsg && <ErrorMsg>{errorMsg}</ErrorMsg>}
              </LoginForm>
            </LoginContainer>
          )
        }}
      </NxtThemeContext.Consumer>
    )
  }
}

export default Login
