import {Component} from 'react'
import {HiFire} from 'react-icons/hi'
import Cookies from 'js-cookie'
import NxtThemeContext from '../../Context'
import {
  TrendingVideosContainer,
  SideBarAndContentContainer,
  TrendingVideosContentContainer,
  TrendingVideosBanner,
  TrendingLogoContainer,
  TrendingTitle,
  TrendingVideosList,
} from './StyledComponents'

import Header from '../Header'
import SideBar from '../SideBar'
import VideoItem from '../VideoItem'
import LoaderBox from '../LoaderBox'

const apiStatus = {
  loading: 'LOADING',
  success: 'SUCCESS',
  failure: 'FAILURE',
}

class TrendingVideos extends Component {
  state = {trendingVideos: [], status: apiStatus.loading}

  componentDidMount() {
    this.getTrendingVideos()
  }

  updateData = data => ({
    channel: {
      name: data.channel.name,
      profileImageUrl: data.channel.profile_image_url,
    },
    id: data.id,
    publishedAt: data.published_at,
    thumbnailUrl: data.thumbnail_url,
    title: data.title,
    viewCount: data.view_count,
  })

  getTrendingVideos = async () => {
    this.setState({status: apiStatus.loading})
    const jwtToken = Cookies.get('jwt_token')
    const options = {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
    }
    const url = 'https://apis.ccbp.in/videos/trending'
    const response = await fetch(url, options)
    const data = await response.json()
    console.log(data.videos)
    if (response.ok) {
        fetchedData = data.videos.map(eachItem=>this.updateData(each))
      this.setState({status: apiStatus.success})
    } else {
      this.setState({status: apiStatus.failure})
    }
  }

  switchRender = theme => {
    const {trendingVideos, status} = this.state
    switch (status) {
      case 'LOADING':
        return <LoaderBox />
      case 'SUCCESS':
        return (
          <TrendingVideosContentContainer>
            <TrendingVideosBanner theme={theme}>
              <TrendingLogoContainer theme={theme}>
                <HiFire />
              </TrendingLogoContainer>
              <TrendingTitle theme={theme}>Trending Videos</TrendingTitle>
            </TrendingVideosBanner>
            <TrendingVideosList>
              {trendingVideos.map(eachItem => (
                <VideoItem key={eachItem.id} details={eachItem} />
              ))}
            </TrendingVideosList>
          </TrendingVideosContentContainer>
        )
      case 'FAILURE':
        return <LoaderBox />
      default:
        return null
    }
  }

  render() {
    return (
      <NxtThemeContext.Consumer>
        {value => {
          const {isDarkTheme} = value
          const theme = isDarkTheme ? 'dark' : ''
          return (
            <TrendingVideosContainer theme={theme} data-testid="savedVideos">
              <Header />
              <SideBarAndContentContainer>
                <SideBar />
                {this.switchRender(theme)}
              </SideBarAndContentContainer>
            </TrendingVideosContainer>
          )
        }}
      </NxtThemeContext.Consumer>
    )
  }
}
export default TrendingVideos
