import { Component } from "react";
import {Link} from 'react-router-dom'
import {AiFillHome} from 'react-icons/ai'
import {HiFire} from 'react-icons/hi'
import {CgPlayListAdd} from 'react-icons/cg'
import {SiYoutubegaming} from 'react-icons/si'

import NavItem from '../NavItem'
import {SideBarContainer, SideNavBar} from './StyledComponents'

const sideNavList = [
  {id: 1, logo: AiFillHome, path: '/', text: 'Home'},
  {id: 2, logo: HiFire, path: '/', text: 'Trending'},
  {id: 3, logo: SiYoutubegaming, path: '/', text: 'Gaming'},
  {id: 4, logo: CgPlayListAdd, path: '/', text: 'Saved Videos'},
]

class SideBar extends Component{
    state={initialNavList: sideNavList}
    render(){
        return (
  <SideBarContainer>
    <li>
      <SideNavBar>
        {sideNavList.map(eachItem => (
          <Link
            key={eachItem.id}
            style={{textDecoration: 'none'}}
            to={eachItem.path}
          >
            <NavItem details={eachItem} />
          </Link>
        ))}
      </SideNavBar>
    </li>
  </SideBarContainer>
)
        }}
export default SideBar
