import NxtThemeContext from '../../Context'
import {SideNavItem, NavItemText} from './StyledComponents'

const NavItem = props => {
  const {details} = props
  const active = details.active ? 'active' : ''

console.log(window.location.pathname)

  return (
    <NxtThemeContext.Consumer>
      {value => {
        const {isDarkTheme, onClickActive} = value
        const changeActiveStatus = () => {
          onClickActive(details.id)
        }
        const theme = isDarkTheme ? 'dark' : ''
        return (
          <SideNavItem
            theme={theme}
            active={active}
            onClick={changeActiveStatus}
          >
            <details.logo />
            <NavItemText theme={theme}>{details.text}</NavItemText>
          </SideNavItem>
        )
      }}
    </NxtThemeContext.Consumer>
  )
}

export default NavItem
