import {Component} from 'react'

import Cookies from 'js-cookie'

import SideBar from '../SideBar'
import Header from '../Header'

import NxtThemeContext from '../../Context'
import {
  VideoItemDetailsContainer,
  VideoItemAndSideBar,
} from './StyledComponents'

const apiStatus = {
  loading: 'LOADING',
  success: 'SUCCESS',
  failure: 'FAILURE',
}

class VideoItemDetails extends Component {
  state = {VideoDetails: {}, status: apiStatus.loading}

  componentDidMount() {
    this.getVideoDetails()
  }

  getVideoDetails = async () => {
    this.setState({status: apiStatus.loading})
    const jwtToken = Cookies.get('jwt_token')
    const {match} = this.props
    const {params} = match
    const {id} = params
    const url = `https://apis.ccbp.in/videos/${id}`
    console.log(url)
    const options = {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
    }
    const response = await fetch(url, options)
    const data = await response.json()
    const fetchedData = data.video_details
    console.log(fetchedData)
    if (response.ok) {
      const updatedData = {
        channel: {
          name: fetchedData.channel.name,
          profileImageUrl: fetchedData.profile_image_url,
          subscriberCount: fetchedData.subscriber_count
        },
      }
      console.log(updatedData)
    }
  }

  render() {
    const {VideoDetails, status} = this.state
    console.log(VideoDetails, status)
    return (
      <NxtThemeContext.Consumer>
        {value => {
          const {isDarkTheme} = value
          const theme = isDarkTheme ? 'dark' : ''
          console.log(theme)
          return (
            <VideoItemDetailsContainer>
              <Header />
              <VideoItemAndSideBar>
                <SideBar />
              </VideoItemAndSideBar>
            </VideoItemDetailsContainer>
          )
        }}
      </NxtThemeContext.Consumer>
    )
  }
}

export default VideoItemDetails
