  }

  render() {
    const {initialNavList} = sideNavList
    return (
      <SideBarContainer>
        <li>
          <SideNavBar>
            {initialNavList.map(eachItem => (
              <Link
                key={eachItem.id}
                style={{textDecoration: 'none'}}
                to={eachItem.path}
              >
                <NavItem details={eachItem} />
              </Link>
            ))}
          </SideNavBar>
        </li>
      </SideBarContainer>
    )
  }
}
