import {withRouter} from 'react-router-dom'
import Cookies from 'js-cookie'
import {Popup} from 'reactjs-popup'
import NxtThemeContext from '../../Context'
import {
  LogoutButton,
  ModalContainer,
  ModalButton,
  ModalText,
} from './StyledComponents'

const Logout = props => (
  <NxtThemeContext.Consumer>
    {value => {
      const {isDarkTheme, toggleModal} = value
      const theme = isDarkTheme ? 'dark' : ''
      const redirectToLoginPage = () => {
        toggleModal()
        Cookies.remove('jwt_token')
        const {history} = props
        history.replace('/login')
      }

      return (
        <Popup
          modal
          trigger={
            <LogoutButton type="button" theme={theme}>
              Logout
            </LogoutButton>
          }
        >
          {close => {
            thitoggleModal
            return (
              <ModalContainer>
                <ModalText>Are you sure you want to logout ?</ModalText>
                <ModalButton onClick={() => close()} type="button">
                  Cancel
                </ModalButton>
                <ModalButton fill onClick={redirectToLoginPage} type="button">
                  Confirm
                </ModalButton>
              </ModalContainer>
            )
          }}
        </Popup>
      )
    }}
  </NxtThemeContext.Consumer>
)

export default withRouter(Logout)
