import {Component} from 'react'

import {Redirect} from 'react-router-dom'

import Cookies from 'js-cookie'

import NxtThemeContext from '../../Context'

import {
  LoginContainer,
  LoginForm,
  NxtWedLogo,
  InputLabel,
  UserInput,
  CheckboxContainer,
  CheckboxLabel,
  InputCheckBox,
  LoginButton,
  ErrorMsg,
} from './StyledContainer'

class Login extends Component {
  state = {
    username: '',
    password: '',
    displayPassword: false,
    displayErrorMsg: false,
    errorMsg: '',
  }

  submitSuccess = jwtToken => {
    Cookies.set('jwt_token', jwtToken, {
      expires: 30,
      path: '/',
    })
    const {history} = this.props
    history.replace('/')
  }

  submitFailure = errorMsg => {
    this.setState(prevState => ({
      displayErrorMsg: !prevState.displayErrorMsg,
      errorMsg,
    }))
  }

  postLoginDetails = async event => {
    event.preventDefault()
    const {username, password} = this.state
    const userDetails = {
      username,
      password,
    }
    console.log(password)
    const url = 'https://apis.ccbp.in/login'
    const options = {
      method: 'POST',
      body: JSON.stringify(userDetails),
    }
    const response = await fetch(url, options)
    const data = await response.json()
    if (response.ok) {
      this.submitSuccess(data.jwt_token)
      this.setState({
        username: '',
        password: '',
        displayErrorMsg: false,
        displayPassword: false,
        errorMsg: '',
      })
    } else {
      this.submitFailure(data.error_msg)
    }
  }



  updatePassword = event => {
    this.setState({password: event.target.value})
  }

  togglePasswordDisplay = () => {
    this.setState(prevState => ({displayPassword: !prevState.displayPassword}))
  }

  render() {
    const {
      username,
      password,
      displayPassword,
      displayErrorMsg,
      errorMsg,
    } = this.state

    const jwtToken = Cookies.get('jwt_token')
    if (jwtToken !== undefined) {
      return <Redirect to="/" />
    }
    return (
      <NxtThemeContext.Consumer>
        {value => {
          const {isDarkTheme} = value
          const theme = isDarkTheme ? 'dark' : ''
          return (
            <LoginContainer theme={theme} onSubmit={this.postLoginDetails}>
              <LoginForm theme={theme}>
                <NxtWedLogo
                  src={
                    isDarkTheme
                      ? 'https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-dark-theme-img.png'
                      : 'https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-light-theme-img.png'
                  }
                  alt="nxt watch logo"
                />
                <InputLabel theme={theme} htmlFor="user_name">
                  USERNAME
                </InputLabel>
                <UserInput
                  theme={theme}
                  type="text"
                  id="user_name"
                  onChange={this.updateUsername}
                  value={username}
                  placeholder="Username"
                />
                <InputLabel theme={theme} htmlFor="password">
                  PASSWORD
                </InputLabel>
                <UserInput
                  theme={theme}
                  type={displayPassword ? 'text' : 'password'}
                  id="password"
                  onChange={this.updatePassword}
                  value={password}
                  placeholder="Password"
                />
                <CheckboxContainer>
                  <InputCheckBox
                    id="show-password"
                    type="checkbox"
                    onClick={this.togglePasswordDisplay}
                  />
                  <CheckboxLabel theme={theme} htmlFor="show-password">
                    Show Password
                  </CheckboxLabel>
                </CheckboxContainer>
                <LoginButton type="submit">Login</LoginButton>
                {displayErrorMsg && <ErrorMsg>*{errorMsg}</ErrorMsg>}
              </LoginForm>
            </LoginContainer>
          )
        }}
      </NxtThemeContext.Consumer>
    )
  }
}

export default Login
