import {Component} from 'react'

import {Route, Switch} from 'react-router-dom'
import {AiFillHome} from 'react-icons/ai'
import {HiFire} from 'react-icons/hi'
import {CgPlayListAdd} from 'react-icons/cg'
import {SiYoutubegaming} from 'react-icons/si'
import NxtThemeContext from './Context'

import ProtectedRoute from './components/ProtectedRoute'

import Login from './components/Login'

import Home from './components/Home'

import VideoItemDetails from './components/VideoItemDetails'

import './App.css'

// custom hook to get the current pathname in React

// Replace your code here
class App extends Component {
  state = {
    isDarkTheme: false,
    sideNavList: [
      {id: 1, logo: AiFillHome, path: '/', text: 'Home'},
      {id: 2, logo: HiFire, path: '/trending', text: 'Trending'},
      {id: 3, logo: SiYoutubegaming, path: '/gaming', text: 'Gaming'},
      {
        id: 4,
        logo: CgPlayListAdd,
        path: '/saved-videos',
        text: 'Saved Videos',
      },
    ],
    savedVideos: [],
  }

  onClickActive = id => {
    const {sideNavList} = this.state
    const newList = sideNavList.map(eachItem =>
      eachItem.id === id
        ? {
            id: eachItem.id,
            logo: eachItem.logo,
            path: eachItem.path,
            text: eachItem.text,
            active: true,
          }
        : {
            id: eachItem.id,
            logo: eachItem.logo,
            path: eachItem.path,
            text: eachItem.text,
            active: false,
          },
    )
    this.setState({sideNavList: newList})
  }

  onClickNotActive = () => {
    const {sideNavList} = this.state
    const newList = sideNavList.map(eachItem => ({
      id: eachItem.id,
      logo: eachItem.logo,
      path: eachItem.path,
      text: eachItem.text,
      active: false,
    }))
    this.setState({sideNavList: newList})
  }

  addToSavedVideos = videoDetails => {
    this.setState(prevState => ({
      savedVideos: [...prevState.savedVideos, videoDetails],
    }))
  }

  toggleTheme = () => {
    this.setState(prevState => ({isDarkTheme: !prevState.isDarkTheme}))
  }

  render() {
    const {isDarkTheme, sideNavList, savedVideos} = this.state
    return (
      <NxtThemeContext.Provider
        value={{
          isDarkTheme,
          toggleTheme: this.toggleTheme,
          sideNavList,
          onClickActive: this.onClickActive,
          onClickNotActive: this.onClickNotActive,
          addToSavedVideos: this.addToSavedVideos,
          savedVideos,
        }}
      >
        <Switch>
          <Route exact path="/login" component={Login} />
          <ProtectedRoute exact path="/" component={Home} />
          <ProtectedRoute
            exact
            path="/videos/:id"
            component={VideoItemDetails}
          />
        </Switch>
      </NxtThemeContext.Provider>
    )
  }
}

export default App
