import {Component} from 'react'
import {Link} from 'react-router-dom'
import NxtThemeContext from "../../Context";
import {AiFillHome} from 'react-icons/ai'
import {HiFire} from 'react-icons/hi'
import {CgPlayListAdd} from 'react-icons/cg'
import {SiYoutubegaming} from 'react-icons/si'

import NavItem from '../NavItem'
import {SideBarContainer, SideNavBar} from './StyledComponents'

const sideNavList = [
  {id: 1, logo: AiFillHome, path: '/', text: 'Home', active: true},
  {id: 2, logo: HiFire, path: '/', text: 'Trending', active: false},
  {id: 3, logo: SiYoutubegaming, path: '/', text: 'Gaming', active: false},
  {id: 4, logo: CgPlayListAdd, path: '/', text: 'Saved Videos', active: false},
]

class SideBar extends Component {
  state = {initialNavList: sideNavList}

  onClickActive = id => {
    const newList = sideNavList.map(eachItem =>
      eachItem.id === id
        ? {
            id: eachItem.id,
            logo: eachItem.logo,
            path: eachItem.path,
            text: eachItem.text,
            active: true,
          }
        : {
            id: eachItem.id,
            logo: eachItem.logo,
            path: eachItem.path,
            text: eachItem.text,
            active: false,
          },
    )
    this.setState({initialNavList: newList})
  }

  render() {
    const {initialNavList} = this.state
    console.log(initialNavList)
    return (
        <NxtThemeContext.Consumer>
            {value=>{
                const {isDarkTheme}=value
                const theme = isDarkTheme?"dark":""
                return
            }}
        </NxtThemeContext.Consumer>

    )
  }
}
export default SideBar
