import {Component} from 'react'
import Cookies from 'js-cookie'
import {IoMdClose} from 'react-icons/io'
import {AiOutlineSearch} from 'react-icons/ai'
import NxtThemeContext from '../../Context'
import LoaderBox from '../LoaderBox'
import Header from '../Header'
import SideBar from '../SideBar'
import {
  HomeContainer,
  SideBarAndContentContainer,
  HomeContentContainer,
  BannerContainer,
  WebSiteLogo,
  BannerText,
  BannerTextContainer,
  GetButton,
  CloseIcon,
  MainContentContainer,
  SearchContainer,
  SearchInputElement,
  SearchButton,
} from './StyledComponents'

const apiStatus = {
  loading: 'LOADING',
  success: 'SUCCESS',
  failure: 'FAILURE',
}

class Home extends Component {
  state = {
    display: true,
    status: apiStatus.loading,
    searchInput: '',
    homeVideosList: [],
  }

  componentDidMount() {
    this.getHomeVideos()
  }

  updateData = data => ({
    channel: {
      name: data.channel.name,
      profileImageUrl: data.channel.profile_image_url,
    },
    id: data.id,
    publishedAt: data.published_at,
  })

  getHomeVideos = async () => {
    const {searchInput} = this.state
    const jwtToken = Cookies.get('jwt_token')
    console.log(jwtToken)
    const options = {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
    }
    const url = `https://apis.ccbp.in/videos/all?search=${searchInput}`
    const response = await fetch(url, options)
    const fetchData = await response.json()
    console.log(fetchData)
    const updatedData = fetchData.videos.map(eachItem =>
      this.updateData(eachItem),
    )
    this.setState({homeVideosList: updatedData})
  }

  closeBanner = () => {
    this.setState({display: false})
  }

  renderVideos = () => {
    const {status, homeVideosList} = this.state
    switch (status) {
      case 'LOADING':
        return <LoaderBox />
      case 'SUCCESS':
        return <ul>
            {homeVideosList.map(eachItem=><li key={eachItem.id}>{}</li>)}
        </ul>
      case 'FAILURE':
        return <LoaderBox />
      default:
        return null
    }
  }

  render() {
    const {display, homeVideosList} = this.state
    console.log(homeVideosList)
    return (
      <NxtThemeContext.Consumer>
        {value => {
          const {isDarkTheme} = value
          const theme = isDarkTheme ? 'dark' : ''

          return (
            <HomeContainer>
              <Header />
              <SideBarAndContentContainer theme={theme}>
                <SideBar />
                <HomeContentContainer>
                  <BannerContainer display={display ? 'display' : ''}>
                    <BannerTextContainer>
                      <WebSiteLogo
                        src="https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-light-theme-img.png"
                        alt="website logo"
                      />
                      <BannerText>
                        Buy Nxt Watch Premium prepaid plans with UPI
                      </BannerText>
                      <GetButton type="button">GET IT NOW</GetButton>
                    </BannerTextContainer>
                    <CloseIcon type="button" onClick={this.closeBanner}>
                      <IoMdClose />
                    </CloseIcon>
                  </BannerContainer>
                  <MainContentContainer>
                    <SearchContainer>
                      <SearchInputElement
                        theme={theme}
                        type="search"
                        placeholder="Search"
                      />
                      <SearchButton theme={theme} type="button">
                        <AiOutlineSearch />
                      </SearchButton>
                    </SearchContainer>
                    {this.renderVideos()}
                  </MainContentContainer>
                </HomeContentContainer>
              </SideBarAndContentContainer>
            </HomeContainer>
          )
        }}
      </NxtThemeContext.Consumer>
    )
  }
}
export default Home
