import {Component} from 'react'
import {Link} from 'react-router-dom'
import {AiFillHome} from 'react-icons/ai'
import {HiFire} from 'react-icons/hi'
import {CgPlayListAdd} from 'react-icons/cg'
import {SiYoutubegaming} from 'react-icons/si'
import NxtThemeContext from '../../Context'

import NavItem from '../NavItem'
import {SideBarContainer, SideNavBar, Heading, ContactIcons, Icon} from './StyledComponents'

const sideNavList = [
  {id: 1, logo: AiFillHome, path: '/', text: 'Home', active: true},
  {id: 2, logo: HiFire, path: '/', text: 'Trending', active: false},
  {id: 3, logo: SiYoutubegaming, path: '/', text: 'Gaming', active: false},
  {id: 4, logo: CgPlayListAdd, path: '/', text: 'Saved Videos', active: false},
]

class SideBar extends Component {
  state = {initialNavList: sideNavList}

  onClickActive = id => {
    const newList = sideNavList.map(eachItem =>
      eachItem.id === id
        ? {
            id: eachItem.id,
            logo: eachItem.logo,
            path: eachItem.path,
            text: eachItem.text,
            active: true,
          }
        : {
            id: eachItem.id,
            logo: eachItem.logo,
            path: eachItem.path,
            text: eachItem.text,
            active: false,
          },
    )
    this.setState({initialNavList: newList})
  }

  render() {
    const {initialNavList} = this.state
    console.log(initialNavList)
    return (
      <NxtThemeContext.Consumer>
        {value => {
          const {isDarkTheme} = value
          const theme = isDarkTheme ? 'dark' : ''
          return (
            <SideBarContainer theme={theme}>
              <li>
                <SideNavBar>
                  {initialNavList.map(eachItem => (
                    <Link
                      key={eachItem.id}
                      style={{textDecoration: 'none'}}
                      to={eachItem.path}
                    >
                      <NavItem
                        onClickActive={this.onClickActive}
                        details={eachItem}
                      />
                    </Link>
                  ))}
                </SideNavBar>
              </li>
              <li>
                <SideNavBar as="div">
                  <Heading theme={theme}>CONTACT US</Heading>
                  <ContactIcons>
                      <Icon src="https://assets.ccbp.in/frontend/react-js/nxt-watch-facebook-logo-img.png" alt="facebook logo"/>
                  </ContactIcons>
                </SideNavBar>
              </li>
            </SideBarContainer>
          )
        }}
      </NxtThemeContext.Consumer>
    )
  }
}
export default SideBar
